import React, {Component} from "react";

import CardDeck from "react-bootstrap/CardDeck";
import Card from "react-bootstrap/Card";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";

//data for post
// import data from "./jobs.json";

export default class Jobslist extends Component {
    jobStatus;
    constructor(props) {
      super(props);
      this.jobStatus = props.status;
      this.state = {
        error: null,
        isLoaded: true,
        items: [],

      };
    }

    componentDidMount() {
    fetch("http://localhost:8080/jobs/status/"+this.jobStatus, {mode:"no-cors"})

    .then(res => res.json())
    .then(
       (result) => {
         // console.log(">>>> "+ result);
         this.setState({

           isLoaded: true,
           items:result

         });
         // console.log("<<<<<<< "+ this.state.items);
       },
       // Note: it's important to handle errors here
       // instead of a catch() block so that we don't swallow
       // exceptions from actual bugs in components.
       (error) => {
         this.setState({
           isLoaded: true,
           error
         });
       }
     )
}

      render() {
        const { error, isLoaded, items } = this.state;
        if (error != null) {
          return <div>Error: {this.jobStatus} {error.message}</div>;
        } else if (!isLoaded) {
          return <div>Loading...</div>;
        } else {
          return (
            <>
              <Container fluid={true}>
                <Row>
                  <CardDeck className=' no-gutters '>
                    {items.map((postData) => {
                      console.log(postData);
                      return (
                        <Card  key={postData.id}>
                          {/* <Card.Img variant="top" src={postData.image} /> */}
                          <Card.Body>
                            <Card.Title >
                              {postData.id + ". " + postData.jobName}
                            </Card.Title>
                            <Card.Subtitle >
                              {postData.add_dt + " " + postData.addCd}
                            </Card.Subtitle>

                            <Card.Text >
                              {postData.jobDesc}
                            </Card.Text>
                          </Card.Body>
                        </Card>
                      );
                    })}
                  </CardDeck>
                </Row>
              </Container>
            </>
          );
        }
      }
    }
